    def next(self):
        # 如果已有持仓，则不操作
        if self.position:
            # 如果出现向下交叉，则平仓
            if self.crossover < 0:
                self.close()
        # 如果没有持仓
        elif self.crossover > 0:
            # 出现向上交叉，买入
            self.buy()

